mapper.delete:
DELETE FROM tb_activity t
WHERE t.id IN

    #{id}

mapper.select:
SELECT 
FROM tb_activity t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.name = #{name}


    AND t.version = #{version}


    AND t.upgrade_notice_article_id = #{upgradeNoticeArticleId}


    AND t.activity_term_article_id = #{activityTermArticleId}


    AND t.privacy_agreement_article_id = #{privacyAgreementArticleId}


    AND t.start_time = #{startTime}


    AND t.end_time = #{endTime}


    AND t.publish_time = #{publishTime}


    AND t.upgrade_purpose = #{upgradePurpose}


    AND t.upgrade_function = #{upgradeFunction}


    AND t.statement = #{statement}


    AND t.state = #{state}


    AND t.total_file_size = #{totalFileSize}


    AND t.baseline = #{baseline}


    AND t.baseline_code = #{baselineCode}


    AND t.description = #{description}


    AND t.create_time = #{createTime}


    AND t.create_by = #{createBy}


    AND t.modify_time = #{modifyTime}


    AND t.modify_by = #{modifyBy}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC
