mapper.delete:
DELETE FROM tb_fixed_config_word_detail t
WHERE t.id IN

    #{id}

mapper.select:
SELECT 
FROM tb_fixed_config_word_detail t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.fixed_config_word_id = #{fixedConfigWordId}


    AND t.config_word_version = #{configWordVersion}


    AND t.start_byte = #{startByte}


    AND t.start_bit = #{startBit}


    AND t.config_word_value = #{configWordValue}


    AND t.depend_ecu = #{dependEcu}


    AND t.depend_ecu_software_pn = #{dependEcuSoftwarePn}


    AND t.description = #{description}


    AND t.create_time = #{createTime}


    AND t.create_by = #{createBy}


    AND t.modify_time = #{modifyTime}


    AND t.modify_by = #{modifyBy}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC
