mapper.delete:
DELETE FROM tb_task t
WHERE t.id IN

    #{id}

mapper.select:
SELECT 
FROM tb_task t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.name = #{name}


    AND t.type = #{type}


    AND t.stage = #{stage}


    AND t.activity_id = #{activityId}


    AND t.target = #{target}


    AND t.start_time = #{startTime}


    AND t.end_time = #{endTime}


    AND t.publish_time = #{publishTime}


    AND t.limit_condition = #{limitCondition}


    AND t.limit_ibs_soc = #{limitIbsSoc}


    AND t.limit_bms_soc = #{limitBmsSoc}


    AND t.notice_type = #{noticeType}


    AND t.upgrade_mode = #{upgradeMode}


    AND t.appointment_time = #{appointmentTime}


    AND t.ecu_try_limit = #{ecuTryLimit}


    AND t.fail_rollback = #{failRollback}


    AND t.adaptation = #{adaptation}


    AND t.baseline_alignment = #{baselineAlignment}


    AND t.version_check = #{versionCheck}


    AND t.part_no_compatible = #{partNoCompatible}


    AND t.vehicle_impact = #{vehicleImpact}


    AND t.full_package_first = #{fullPackageFirst}


    AND t.state = #{state}


    AND t.description = #{description}


    AND t.create_time = #{createTime}


    AND t.create_by = #{createBy}


    AND t.modify_time = #{modifyTime}


    AND t.modify_by = #{modifyBy}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC
