mapper.delete:
DELETE FROM tb_task_vehicle t
WHERE t.id IN

    #{id}

mapper.select:
SELECT 
FROM tb_task_vehicle t
WHERE t.task_id = #{taskId}
AND t.vin = #{vin}
LIMIT 1

SELECT 
FROM tb_task_vehicle t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.activity_id = #{activityId}


    AND t.task_id = #{taskId}


    AND t.vin = #{vin}


    AND t.state = #{state}


    AND t.result_code = #{resultCode}


    AND t.description = #{description}


    AND t.create_time = #{createTime}


    AND t.create_by = #{createBy}


    AND t.modify_time = #{modifyTime}


    AND t.modify_by = #{modifyBy}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC

SELECT FROM tb_task_vehicle t WHERE t.row_valid = 1
AND t.vin = #{vin}
ORDER BY t.id DESC
