mapper.delete:
DELETE FROM tb_veh_status t
WHERE t.id IN

    #{id}

mapper.select:
SELECT 
FROM tb_veh_status t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.vin = #{vin}


    AND t.report_time = #{reportTime}


    AND t.baseline_code = #{baselineCode}


    AND t.baseline_alignment = #{baselineAlignment}


    AND t.ecu_info = #{ecuInfo}


    AND t.activity_id = #{activityId}


    AND t.task_id = #{taskId}


    AND t.config_word = #{configWord}


    AND t.master_version = #{masterVersion}


    AND t.description = #{description}


    AND t.create_time = #{createTime}


    AND t.create_by = #{createBy}


    AND t.modify_time = #{modifyTime}


    AND t.modify_by = #{modifyBy}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC
